import React, { useState } from 'react'
export default function TaskList() {

  const [listItems, setListItems] = useState([]) //[ declared thing, how you set it to something/override]

  function handleInputChange(event, index) {
    const newListItems = [...listItems]
    newListItems[index] = event.target.value
    setListItems(newListItems)
    event.target.style.height = 'inherit';
    event.target.style.height = `${e.target.scrollHeight}px`; 

  }

  function createNewTask(event) {
    const text = event.target.value
    setListItems([...listItems, text])

  }


  return (
    <div className="TaskList">
      <h1>Task List</h1>
      <ul>
        {listItems.map((item, index) => (
          <li key={index}>
            <div className='input-wrapper'>
              <textarea value={item} onChange={(event) => {handleInputChange(event, index)}} className="task" autoFocus>
              </textarea>
            </div>
          </li>
        ))}
      </ul>
      <textarea value={""} placeholder='+ New Task' onChange={(e) => createNewTask(e)} onKeyPress={(key) => console.log(key)}></textarea> 
    </div>
  );


}
