import React, { useState } from 'react'
import '../styles/Lists.css';
import ListOptions from './ListOptions';
import EditListMode from './EditListMode';

export default function TaskList() {

  const[listItems, setListItems] = useState([]) //[ declared thing, how you set it to something/override]
  const[checked, setChecked]= useState(false)
  const [optionsDisplayed, setDisplayed]=useState(true)
  const[title,setTitle]=useState('Title')

  function handleTaskChange(event, index) {
    const newListItems = [...listItems]
    newListItems[index] = event.target.value
    setListItems(newListItems)
    event.target.style.height = '1em';
    event.target.style.height = `${event.target.scrollHeight}px`; 

  }
  function handleTitleChange(event){
    setTitle(event.target.value)
  }

  function handleCheckBoxChange(event,index){
    const currCheckbox=event.target.value
    setChecked()

  }

  function handleUserClickOut(event,index){
    const taskContent=event.target.value
    if (taskContent.length===0){
      const newListItems=[...listItems]
      newListItems.pop(index)
      setListItems(newListItems)
    }
  }

  function handleOptionsClick(event){
    setDisplayed(!displayed)
  }

  function createNewTask(event) {
    const text = event.target.value
    setListItems([...listItems, text])
  }



  return (
    <>
      <EditListMode/>
      <div className="TaskList">
        <div className='list-header'>      
          <input value={title} onChange={(event)=>{handleTitleChange(event)}} className="list-name"></input>
          <button onClick={(event)=>{handleOptionsClick(event)}}>edit</button>
          </div>

          {displayed && <ListOptions/>}
        <ul>
          {listItems.map((item, index) => (
            <li key={index}>
              <input 
                type= 'checkbox'
                checked={checked} //checked left= if box is checked or not, checked right= variable storing T/F for checked
                onChange={(event) => {handleCheckBoxChange(event, index)}} className="task" autoFocus />
                {/* <textarea rows="1" onBlur={(event)=>{handleUserClickOut(event,index)}}  onChange={(event) => {handleTaskChange(event, index)}} className="task" autoFocus /> */}
                <div>{item}</div>
            </li>
          ))}
          <li>
            <textarea rows="1" value={""} placeholder='+ New Task' onClick={(e) => createNewTask(e)} /> 
            <input 
                disabled='true'
                type= 'checkbox'
                id='invisible-checkbox'
                />
          </li>
        </ul>

      </div>
    </>
  );
}
