        <div className="list-options">
            <h1>List Options</h1>
            <ul>
                <li>
                    <button onClick={(event)=>handleDelete(event)} className="edit-option">Delete List</button>
                </li>
                <li>
                    <button onClick={(event)=>handleEdit(event)}className="edit-option">Edit List</button>
