import TaskList from "./TaskList"
import "../styles/TaskListContainer.css"
import { useState } from "react"

export default function AllTaskLists() {
  const [taskLists, setTaskLists] = useState([
    {
      name: "Task List 1",
      currentItems: [
        "Task 1",
      ],
      completedItems: [
        "Task 2",
      ],
      editModeDisplayed: false,
      optionsDisplayed: false,
    },
    {
      name: "Task List 2",
      currentItems: [
        "Task 3",
      ],
      completedItems: [
        "Task 4",
      ],
      editModeDisplayed: false,
      optionsDisplayed: false,
    },
  ])

  const handleAddTask = () => {
    setTasks([
      ...tasks,
      {
        name: "",
        currentItems: [],
        completedItems: [],
        editModeDisplayed: false,
        optionsDisplayed: false,
      }
    ])
  }

  return (
    <div className="lists-container">

      {tasks.map((task, index) => {
        return (
          <TaskList
            key={index}
            task={task}
            taskLists={taskLists}
            setTasks={setTasks}
            index={index}
          />
        )
      })}
      <TaskList/>
      <button className="add-list-button" onClick={() => handleAddTask}>Add Task</button>
    </div>
  )
}
