import TaskList from "./TaskList"
import "../styles/AllTaskLists.css"
import { useEffect, useState } from "react"

export default function AllTaskLists() {
  useEffect(() => {
    const taskLists = JSON.parse(localStorage.getItem("taskLists"));
    if (taskLists) {
      setTaskLists(taskLists);
    }
  }, []);
  const [taskLists, setTaskLists] = useState([
    {
      title: "Task List 1",
      currentItems: [
        "Task 1",
        'Task 2',
        'Task 3',
      ],
      checkedItems: [
        "Task 2",
      ],
      editModeDisplayed: false,
      optionsDisplayed: false,
    },
    {
      title: "Task List 2",
      currentItems: [
        "Task 3",
      ],
      checkedItems: [
        "Task 4",
      ],
      editModeDisplayed: false,
      optionsDisplayed: false,
    },
  ])

  function handleNewList() {
    setTaskLists([
      ...taskLists,
      {
        name: "Title",
        currentItems: [],
        checkedItems: [],
        editModeDisplayed: true,
        optionsDisplayed: false,
      }
    ])
  }
  useEffect(() => {
    localStorage.setItem("taskLists", JSON.stringify(taskLists))
  } , [taskLists]);


  return (
    <div className="all-lists-container">

      {taskLists.map((taskList, index) => {
        return (
          <TaskList
            listIndex={index}
            taskLists={taskLists}
            setTaskLists={setTaskLists}
          />
        )
      })}
      <button onClick={(event)=>{handleNewList(event)}}>New List</button>
      {/* <TaskList/>
      <TaskList/> */}
      {/* <button className="add-list-button" onClick={() => handleAddTask}>Add Task</button> */}
    </div>
  )
}
